import pickle
from sqlalchemy import create_engine
import pandas as pd
import pathlib2 as pathlib
import torch
import nltk
import spacy
from random import shuffle
nlp = spacy.load('de_core_news_lg')

cwd=pathlib.Path().cwd()
# print(cwd)
db_file=cwd.joinpath('chatbot/cards.db')

def transform_to_tensor(l):
    translation_table = dict.fromkeys(map(ord, '[] '), None)
    l=l.translate(translation_table)
    l=l.split(',')
    l=[float(x) for x in l]
    return torch.FloatTensor(l)

def read_emd():
    engine = create_engine('sqlite:///'+db_file.as_posix(), echo=False)
    conn=engine.connect()
    fquery='''SELECT * FROM cards'''
    #q=create_query("*")
    query=pd.read_sql_query(fquery, con=engine)
    df = pd.DataFrame(query)
    # df['q_tensor']=df.q_tensor.transform(transform_to_tensor)
    df['a_tensor']=df.a_tensor.transform(transform_to_tensor)
    conn.close()
    engine.dispose()
    return df

def spacy_tokenizer(question):
    doc = nlp(question)
    return_dict={}
    for token in doc:
        # if token.pos_ != "PUNCT":
        if token.pos_ in ["NOUN", "PROPN"]:
            text=token.text
            return_dict['contains({})'.format(text.lower())] = True
        else:
            return_dict['contains({})'.format(token.lemma_.lower())] = True
    return return_dict

